pragma solidity ^0.8.18;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../utils/Errors.sol";

library TransferUtils {
  uint256 private constant _TRANSFER_GAS_LIMIT = 100000;

  function transfer(address token, address receiver, uint256 amount) external {
    if (amount == 0) {
      return;
    }

    (bool success, ) = nonRevertingTransferWithGasLimit(IERC20(token), receiver, amount, _TRANSFER_GAS_LIMIT);

    if (success) {
      return;
    }

    revert Errors.TokenTransferError(token, receiver, amount);
  }

  function nonRevertingTransferWithGasLimit(
    IERC20 token,
    address to,
    uint256 amount,
    uint256 gasLimit
  ) internal returns (bool, bytes memory) {
    bytes memory data = abi.encodeWithSelector(token.transfer.selector, to, amount);
    (bool success, bytes memory returndata) = address(token).call{ gas: gasLimit }(data);

    if (success) {
      // some tokens do not revert on a failed transfer, they will return a boolean instead
      // validate that the returned boolean is true, otherwise indicate that the token transfer failed
      if (returndata.length > 0 && !abi.decode(returndata, (bool))) {
        return (false, returndata);
      }

      // transfers on some tokens do not return a boolean value, they will just revert if a transfer fails
      // for these tokens, if success is true then the transfer should have completed
      return (true, returndata);
    }

    return (false, returndata);
  }
}
","keccak256":"0xfd30492fa6484aec6ae8b64c25c9ad68e0987fb683a26f0f915269212e03bb97","license":"MIT"},"contracts/vault/DaoVault.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "hardhat/console.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../utils/TransferUtils.sol";
import "../utils/RoleKeys.sol";
import "../utils/Errors.sol";
import "../storage/RoleAccessControl.sol";

contract DaoVault is Ownable {
  using SafeERC20 for IERC20;

  constructor(address initialOwner) Ownable(initialOwner) {}

  receive() external payable {}

  fallback() external payable {}

  function getBalance() public view returns (uint) {
    return address(this).balance;
  }

  function transferETH(address payable recipient, uint amount) external {
    recipient.transfer(amount);
    // (bool success, ) = recipient.call{ value: amount }(new bytes(0));
    // require(success, "SET");
  }

  mapping(address => uint256) public tokenBalances;

  function transferToken(address token, address receiver, uint256 amount) external {
    console.log("token==========:", token, "receiver:", receiver);
    require(IERC20(token).transfer(receiver, amount), "transfer failed");
  }

  function transferOut(address token, address receiver, uint256 amount) external onlyOwner {
    if (receiver == address(this)) {
      revert Errors.AddressSelfNotSupported(receiver);
    }

    // console.log("transfer=========msg.sender:", msg.sender);
    // require(msg.sender == owner, "Only owner can transfer");

    TransferUtils.transfer(token, receiver, amount);
    tokenBalances[token] = IERC20(token).balanceOf(address(this));
  }

  function getTransferInAmount(address token) external returns (uint256) {
    uint256 prevBalance = tokenBalances[token];
    uint256 nextBalance = IERC20(token).balanceOf(address(this));
    tokenBalances[token] = nextBalance;
    return nextBalance - prevBalance;
  }
}
